INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (1, 1, '오늘 진짜 개피곤하다. 아침부터 수업 3개 연속으로 들었는데, 교수님들이 다 각자 과제를 내주시네. 점심도 건너뛰고 도서관에서 과제 하다가 머리 터질 뻔… 커피만 3잔 마신 것 같아. 집에 와서는 너무 힘들어서 그냥 눌렀음. 내일도 똑같이 반복될 생각하니… 막막하다 진짜.', '2025-04-01', 1, 1, 1, 1, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (2, 1, '날씨 겁나 좋아서 산책 나갔는데 기분이 좀 나아졌다. 공원에 벚꽃도 피고 바람도 시원해서 좋았음. 혼자 걷는데 옆에서 강아지 놀고 있는 거 보고 잠시 웃었네. 그래도 요즘 우울한 기분이 조금은 해소된 듯.', '2025-04-02', 2, 2, 2, 2, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (3, 1, '친구 만나서 카페 갔는데 웃긴 이야기하다가 기분 전환됐다. 나 요즘 우울해서 말도 별로 안 했는데, 오늘은 오랜만에 속이 후련해짐. 다음에 또 만나기로 했는데 기대됨.', '2025-04-03', 3, 3, 3, 3, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (4, 1, '오늘은 왜 이렇게 뭐 하나 하기 싫은지… 알람 끄고 계속 누워있었음. 밥도 먹기 귀찮고, 그냥 하루 종일 폰만 뒤적거렸네. 나 왜 이럴까?', '2025-04-04', 4, 4, 4, 4, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (5, 1, '새로 산 책 읽었는데 진짜 좋다. 작가가 쓴 문장 하나하나가 마음에 와닿아서 계속 생각나네. 오랜만에 머리도 식히고 기분도 전환된 기분.', '2025-04-05', 5, 5, 5, 5, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (6, 1, '오늘 약속 있었는데 비 와서 취소됐음. 계획한 거 다 틀어져서 기분 더러웠다. 창문 열고 비 내리는 거 보다가 그냥 잠들었네.', '2025-04-06', 6, 6, 6, 6, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (7, 1, '엄마랑 통화했는데 오랜만에 따뜻한 말씀 해주셔서 눈물 날 뻔. 평소엔 바빠서 연락도 잘 안 했는데… 앞으로는 자주 해야겠다.', '2025-04-07', 7, 7, 7, 7, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (8, 1, '오늘은 일찍 잠 자려고 노력했음. 피곤해서 그런지 금방 잠들었고, 아침에 좀 개운한 기분. 역시 수면이 최고야.', '2025-04-08', 8, 8, 8, 8, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (9, 1, '요리 도전했다가 완전 실패함. 레시피대로 했는데 왜 이렇게 맛이 없는지… 그래도 재밌었으니 됐음. 다음엔 더 잘해보자.', '2025-04-09', 9, 9, 9, 9, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (10, 1, '하루 종일 비 오니까 기분도 다운됨. 그냥 커피 마시면서 창밖 보다가 음악 듣고 있었는데, 좀 나아졌다.', '2025-04-10', 10, 10, 10, 10, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (11, 1, '오늘 운동 갔다 왔는데 기분 좋네. 땀 빼고 나니 머리도 맑아지는 느낌. 앞으로 자주 가야겠다.', '2025-04-11', 11, 11, 11, 11, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (12, 1, '친구가 선물해줘서 기분 좋았다. 별거 아닌데도 이런 거 받으면 진짜 행복함.', '2025-04-12', 12, 12, 12, 12, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (13, 1, '오늘 집 청소했다. 쓰레기 버리고 정리하니 기분이 상쾌해짐. 이제 좀 살만하네.', '2025-04-13', 13, 13, 13, 13, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (14, 1, '오늘 모임에서 새로운 사람들 만났는데 재밌었음. 다양한 이야기 듣다 보니 시간 가는 줄 몰랐네.', '2025-04-14', 14, 14, 14, 14, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (15, 1, '영화 봤는데 진짜 감동적이었음. 끝나고 한참 동안 여운이 남더라. 이런 영화 좋아.', '2025-04-15', 15, 15, 15, 15, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (16, 1, '갑자기 계획이 바뀌어서 당황스러웠음. 그래도 새로운 일정이 더 마음에 들 것 같아.', '2025-04-16', 16, 16, 16, 16, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (17, 1, '오랜만에 일기 써봤다. 적어놓고 보니 별거 아닌 일들인데, 그래도 마음이 정리되는 느낌.', '2025-04-17', 17, 17, 17, 17, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (18, 1, '하루 종일 아무것도 안 하고 혼자 조용히 보냈음. 가끔은 이런 날도 필요한 거 같아.', '2025-04-18', 18, 18, 18, 18, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (19, 1, '새로운 레시피로 요리해봤는데 생각보다 괜찮게 나옴. 뿌듯하네.', '2025-04-19', 19, 19, 19, 19, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (20, 1, '비 오니까 또 우울해졌다. 그래도 어제보다는 나은 거 같아.', '2025-04-20', 20, 20, 20, 20, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (21, 1, '운동 또 갔다 왔음. 이제 습관이 되어가는 거 같아. 좋은 변화인 듯.', '2025-04-21', 21, 21, 21, 21, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (22, 1, '오늘은 내가 친구한테 선물했는데, 반응이 좋아서 기분 좋았다.', '2025-04-22', 22, 22, 22, 22, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (23, 1, '또 청소했다. 이제 좀 더 깔끔해진 거 같아 만족스럽네.', '2025-04-23', 23, 23, 23, 23, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (24, 1, '새로운 사람들과 대화하니 재밌네. 다양한 생각을 접할 수 있어 좋다.', '2025-04-24', 24, 24, 24, 24, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (25, 1, '영화 또 봤는데 이번에도 좋았음. 요즘 영화 보는 게 최고의 힐링이야.', '2025-04-25', 25, 25, 25, 25, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (26, 1, '계획이 또 바뀌었지만, 이번엔 별로 신경 안 썼음. 유연해지는 중?', '2025-04-26', 26, 26, 26, 26, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (27, 1, '일기 쓰는 게 점점 습관이 되어간다. 오늘은 무슨 일이 있었더라…?', '2025-04-27', 27, 27, 27, 27, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (28, 1, '하루 종일 아무 생각 없이 보냈는데, 이게 진짜 휴식인 거 같다.', '2025-04-28', 28, 28, 28, 28, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (29, 1, '또 새로운 요리 도전했는데 이번엔 성공! 기분 좋다.', '2025-04-29', 29, 29, 29, 29, NOW(), NOW());
INSERT INTO diary (diary_id, member_id, content, written_date, emotion_id, transform_diary_id, diary_feedback_id, depression_id, created_at, updated_at) VALUES (30, 1, '4월의 마지막 날. 이번 달은 정말 많은 일이 있었네. 내일부터는 새로운 마음으로 시작해보자.', '2025-04-30', 30, 30, 30, 30, NOW(), NOW());
